      symbolStats[t.symbol].pnl += t.pnl;
      if (t.pnl > 0) symbolStats[t.symbol].wins++;
      else symbolStats[t.symbol].losses++;
    });

    const sortedSymbols = Object.entries(symbolStats).sort((a, b) => b[1].pnl - a[1].pnl);
    if (sortedSymbols.length > 0) {
