                </button>
                <button
                  onClick={() => applyFilters('winners')}
                  className={`px-4 py-2 rounded-lg transition ${filter === 'winners' ? 'bg-cyan-500 text-white' : 'bg-gray-800 text-gray-300 hover:bg-gray-700'}`}
