// ManualTradeForm Component
const ManualTradeForm = ({ onSubmit, onClose }) => {
  const [formData, setFormData] = useState({
    date: new Date().toISOString().split('T')[0],
    symbol: '',
    side: 'long',
    quantity: '',
    entryPrice: '',
    exitPrice: '',
    fees: '',
    notes: '',
    duration: ''
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit({
      ...formData,
      quantity: parseFloat(formData.quantity),
      entryPrice: parseFloat(formData.entryPrice),
      exitPrice: parseFloat(formData.exitPrice),
      fees: parseFloat(formData.fees || 0)
    });
  };

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <div className="bg-gray-800 p-6 rounded-lg max-w-md w-full">
        <h2 className="text-xl font-bold mb-4">Add Manual Trade</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
          <input name="date" type="date" value={formData.date} onChange={handleChange} className="w-full p-2 bg-gray-700 rounded" required />
          <input name="symbol" value={formData.symbol} onChange={handleChange} placeholder="Symbol" className="w-full p-2 bg-gray-700 rounded" required />
          <select name="side" value={formData.side} onChange={handleChange} className="w-full p-2 bg-gray-700 rounded">
            <option value="long">Long</option>
            <option value="short">Short</option>
          </select>
          <input name="quantity" type="number" value={formData.quantity} onChange={handleChange} placeholder="Quantity" className="w-full p-2 bg-gray-700 rounded" required />
          <input name="entryPrice" type="number" step="0.01" value={formData.entryPrice} onChange={handleChange} placeholder="Entry Price" className="w-full p-2 bg-gray-700 rounded" required />
          <input name="exitPrice" type="number" step="0.01" value={formData.exitPrice} onChange={handleChange} placeholder="Exit Price" className="w-full p-2 bg-gray-700 rounded" required />
          <input name="fees" type="number" step="0.01" value={formData.fees} onChange={handleChange} placeholder="Fees" className="w-full p-2 bg-gray-700 rounded" />
          <textarea name="notes" value={formData.notes} onChange={handleChange} placeholder="Notes" className="w-full p-2 bg-gray-700 rounded" rows="3" />
          <textarea name="tradeNotes" value={formData.tradeNotes || ''} onChange={handleChange} placeholder="Trade Notes" className="w-full p-2 bg-gray-700 rounded" rows="3" />

          <div>
            <label className="block text-sm mb-2">Tags</label>
            <select
              multiple
              value={formData.tags || []}
              onChange={(e) => {
                const selected = Array.from(e.target.selectedOptions, option => option.value);
                setFormData({ ...formData, tags: selected });
              }}
              className="w-full p-2 bg-gray-700 rounded"
            >
              {availableTags.map(tag => (
                <option key={tag} value={tag}>{tag}</option>
              ))}
            </select>
          </div>
          <div className="flex gap-4">
            <button type="submit" className="px-4 py-2 bg-cyan-500 rounded text-white">Add</button>
            <button type="button" onClick={onClose} className="px-4 py-2 bg-gray-600 rounded text-white">Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
};

