//  Add the three components HERE

const CalendarHeatmap = ({ data, theme }) => {
  const getColor = (pnl) => {
    if (pnl === 0 || !pnl) return theme === 'dark' ? '#1f2937' : '#e5e7eb';
    if (pnl > 500) return '#10b981';
    if (pnl > 200) return '#34d399';
    if (pnl > 50)  return '#6ee7b7';
    if (pnl > 0)   return '#a7f3d0';
    if (pnl < -500) return '#ef4444';
    if (pnl < -200) return '#f87171';
    if (pnl < -50)  return '#fca5a5';
    return '#fecaca';
  };

  return (
    <div className="overflow-x-auto">
      <div className="grid grid-cols-7 gap-1 text-xs">
        {data?.slice(-35).map((day, i) => (
          <div
            key={i}
            className="w-6 h-6 rounded-sm flex items-center justify-center"
            style={{ backgroundColor: getColor(day.pnl) }}
            title={`${day.date || 'N/A'}: $${(day.pnl || 0).toFixed(2)}`}
          />
        ))}
      </div>
      <div className="mt-4 text-xs text-gray-500 flex gap-4">
        <span>Less</span>
        <div className="flex gap-1">
          {['#1f2937', '#fecaca', '#f87171', '#ef4444'].map(c => (
            <div key={c} className="w-4 h-4 rounded-sm" style={{ backgroundColor: c }} />
          ))}
        </div>
        <span>More</span>
      </div>
    </div>
  );
};

