import React, { useState, useEffect, useMemo } from 'react';
import {
  Upload, TrendingUp, DollarSign, BarChart3, Calendar, Download,
  Plus, X, Filter as FilterIcon, TrendingDown, Edit2, Tag, Target,
  Image as ImageIcon, AlertCircle, Search, ChevronLeft, ChevronRight,
  Lightbulb, Sun, Moon, Trash2, CheckSquare
} from 'lucide-react';
import {
  LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid,
  Tooltip, ResponsiveContainer
} from 'recharts';

const TradingJournal = () => {
  const [trades, setTrades] = useState([]);
  const [filteredTrades, setFilteredTrades] = useState([]);
  const [filter, setFilter] = useState('all');
  const [sortBy, setSortBy] = useState('date');
  const [sortOrder, setSortOrder] = useState('desc');
  const [uploadNotification, setUploadNotification] = useState(null);
  const [showTradeForm, setShowTradeForm] = useState(false);
  const [dateRange, setDateRange] = useState('all');
  const [customDateStart, setCustomDateStart] = useState('');
  const [customDateEnd, setCustomDateEnd] = useState('');
  const [currentPage, setCurrentPage] = useState(1);
  const tradesPerPage = 25;
  const [availableTags, setAvailableTags] = useState(['Breakout', 'Reversal', 'Trend Following', 'Scalp', 'Swing', 'News']);
  const [selectedTags, setSelectedTags] = useState([]);
  const [editingTrade, setEditingTrade] = useState(null);
  const [showGoalsModal, setShowGoalsModal] = useState(false);
  const [dailyGoals, setDailyGoals] = useState({ maxLoss: 500, targetProfit: 1000, maxTrades: 10 });
  const [showImageModal, setShowImageModal] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [replayMode, setReplayMode] = useState(false);
  const [replayIndex, setReplayIndex] = useState(0);
  const [watchlist, setWatchlist] = useState([]);
  const [theme, setTheme] = useState('dark');
  const [selectedTrades, setSelectedTrades] = useState(new Set());
  const [bulkMode, setBulkMode] = useState(false);

  // ────────────────────────────────────────────────
  //  Load from localStorage
  // ────────────────────────────────────────────────
  useEffect(() => {
    try {
      const stored = localStorage.getItem('tradingJournalData');
      if (stored) {
        const parsed = JSON.parse(stored);
        if (Array.isArray(parsed)) {
          setTrades(parsed);
          setFilteredTrades(parsed);
        }
      }
    } catch (err) {
      console.error('Failed to load trades', err);
    }

    try {
      const storedTags = localStorage.getItem('availableTags');
      if (storedTags) setAvailableTags(JSON.parse(storedTags));
    } catch (err) {}

    try {
      const storedGoals = localStorage.getItem('dailyGoals');
      if (storedGoals) setDailyGoals(JSON.parse(storedGoals));
    } catch (err) {}

    try {
      const storedWatchlist = localStorage.getItem('watchlist');
      if (storedWatchlist) setWatchlist(JSON.parse(storedWatchlist));
    } catch (err) {}

    try {
      const storedTheme = localStorage.getItem('theme');
      if (storedTheme) setTheme(storedTheme);
    } catch (err) {}
  }, []);

  // Save to localStorage
  useEffect(() => { localStorage.setItem('tradingJournalData',   JSON.stringify(trades));        }, [trades]);
  useEffect(() => { localStorage.setItem('availableTags',      JSON.stringify(availableTags));   }, [availableTags]);
  useEffect(() => { localStorage.setItem('dailyGoals',         JSON.stringify(dailyGoals));      }, [dailyGoals]);
  useEffect(() => { localStorage.setItem('watchlist',          JSON.stringify(watchlist));       }, [watchlist]);
  useEffect(() => { localStorage.setItem('theme',              theme);                           }, [theme]);

  // ────────────────────────────────────────────────
  //  Pagination helpers
  // ────────────────────────────────────────────────
  const totalPages = Math.ceil(filteredTrades.length / tradesPerPage);

  const prevPage = () => {
    if (currentPage > 1) setCurrentPage(currentPage - 1);
  };

  const nextPage = () => {
    if (currentPage < totalPages) setCurrentPage(currentPage + 1);
  };

  // ────────────────────────────────────────────────
  //  CSV Parsing & Upload
  // ────────────────────────────────────────────────
  const parseCSV = (text) => {
    const lines = text.replace(/\r\n/g, '\n').split('\n').filter(Boolean);
    if (lines.length < 2) return [];

    const headers = lines[0].split(',').map(h => h.trim().toLowerCase().replace(/\r/g, ''));
    const data = [];

    for (let i = 1; i < lines.length; i++) {
      const values = lines[i].split(',').map(v => v.trim().replace(/\r/g, ''));
      const trade = {};

      headers.forEach((header, idx) => {
        trade[header] = values[idx] || '';
      });

      const parsePnL = (str) => {
        if (!str) return 0;
        let clean = str.replace(/\$/g, '').replace(/\s/g, '');
        if (clean.startsWith('(') && clean.endsWith(')')) clean = '-' + clean.slice(1, -1);
        return parseFloat(clean) || 0;
      };

      const formatDate = (ts) => ts ? ts.split(' ')[0] : '';
      const getTime   = (ts) => ts && ts.includes(' ') ? ts.split(' ')[1]?.substring(0,5) : '';

      let side = 'unknown';
      if (trade.boughttimestamp && trade.soldtimestamp) {
        const buyId = parseInt(trade.buyfillid || 0);
        const sellId = parseInt(trade.sellfillid || 0);
        side = buyId < sellId ? 'long' : 'short';
      }

      const normalized = {
        id: trade.id || trade.trade_id || trade.buyfillid || `trade_${Date.now()}_${i}`,
        date: formatDate(trade.boughttimestamp || trade.soldtimestamp || trade.date || ''),
        time: getTime(trade.boughttimestamp || trade.soldtimestamp || ''),
        symbol: trade.symbol || trade.ticker || trade.stock || '',
        side: trade.side || side,
        quantity: parseFloat(trade.quantity || trade.qty || trade.shares || 0),
        entryPrice: parseFloat(trade.entry_price || trade.buyprice || trade.entry || 0),
        exitPrice: parseFloat(trade.exit_price || trade.sellprice || trade.exit || 0),
        pnl: parsePnL(trade.pnl || trade.profit_loss || trade.profit || '0'),
        fees: parseFloat(trade.fees || trade.commission || 0),
        notes: trade.notes || trade.comments || '',
        strategy: trade.strategy || trade.setup || '',
        duration: trade.duration || '',
        tags: trade.tags ? trade.tags.split(';').map(t => t.trim()) : [],
        tradeNotes: '',
        screenshots: []
      };

      // Fallback PnL calculation if missing
      if (!normalized.pnl && normalized.entryPrice && normalized.exitPrice && normalized.quantity) {
        if (['long','buy'].includes(normalized.side.toLowerCase())) {
          normalized.pnl = (normalized.exitPrice - normalized.entryPrice) * normalized.quantity;
        } else {
          normalized.pnl = (normalized.entryPrice - normalized.exitPrice) * normalized.quantity;
        }
        normalized.pnl -= normalized.fees;
      }

      data.push(normalized);
    }

    return data;
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const reader = new FileReader();
    reader.onload = (ev) => {
      const parsed = parseCSV(ev.target.result);
      const existing = new Set(trades.map(t => t.id));
      const newOnes = parsed.filter(t => !existing.has(t.id));

      if (newOnes.length === 0) {
        setUploadNotification({ type: 'warning', message: 'No new trades found.' });
        setTimeout(() => setUploadNotification(null), 4000);
        return;
      }

      setUploadNotification({
        type: 'confirm',
        newCount: newOnes.length,
        onConfirm: () => {
          setTrades(prev => [...prev, ...newOnes]);
          setUploadNotification({ type: 'success', message: `Added ${newOnes.length} new trades` });
          setTimeout(() => setUploadNotification(null), 4000);
        },
        onCancel: () => setUploadNotification(null)
      });
    };
    reader.readAsText(file);
    e.target.value = '';
  };

  const exportToCSV = () => {
    const headers = ['ID','Date','Time','Symbol','Side','Quantity','Entry','Exit','PnL','Fees','Notes','Strategy','Tags'];
    const rows = trades.map(t => [
      t.id,
      t.date,
      t.time,
      t.symbol,
      t.side,
      t.quantity,
      t.entryPrice,
      t.exitPrice,
      t.pnl,
      t.fees,
      (t.notes || '').replace(/"/g,'""'),
      (t.strategy || ''),
      (t.tags || []).join(';')
    ].map(v => `"${v}"`).join(','));

    const csv = [headers.join(','), ...rows].join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = 'trading-journal.csv';
    a.click();
    URL.revokeObjectURL(url);
  };

  // ────────────────────────────────────────────────
  //  Filtering & Sorting
  // ────────────────────────────────────────────────
  const applyAllFilters = (tradeList = trades) => {
    let result = [...tradeList];

    // Search
    if (searchQuery.trim()) {
      const q = searchQuery.toLowerCase();
      result = result.filter(t =>
        t.symbol.toLowerCase().includes(q) ||
        (t.notes   || '').toLowerCase().includes(q) ||
        (t.tradeNotes || '').toLowerCase().includes(q) ||
        (t.tags || []).some(tag => tag.toLowerCase().includes(q))
      );
    }

    // Date range
    if (dateRange !== 'all') {
      const now = new Date();
      let start;
      if (dateRange === 'week')  start = new Date(now - 7*86400000);
      if (dateRange === 'month') start = new Date(now - 30*86400000);
      if (dateRange === 'custom' && customDateStart && customDateEnd) {
        start = new Date(customDateStart);
        const end = new Date(customDateEnd);
        result = result.filter(t => {
          const d = new Date(t.date);
          return d >= start && d <= end;
        });
      } else if (start) {
        result = result.filter(t => new Date(t.date) >= start);
      }
    }

    // Tags
    if (selectedTags.length > 0) {
      result = result.filter(t => t.tags?.some(tag => selectedTags.includes(tag)));
    }

    // Win / Loss filter
    if (filter === 'wins')   result = result.filter(t => t.pnl > 0);
    if (filter === 'losses') result = result.filter(t => t.pnl < 0);

    // Sorting
    result.sort((a, b) => {
      let va = a[sortBy], vb = b[sortBy];
      if (sortBy === 'date') {
        va = new Date(a.date);
        vb = new Date(b.date);
      } else if (sortBy === 'pnl' || sortBy === 'quantity') {
        va = Number(va);
        vb = Number(vb);
      }
      if (va > vb) return sortOrder === 'asc' ? 1 : -1;
      if (va < vb) return sortOrder === 'asc' ? -1 : 1;
      return 0;
    });

    setFilteredTrades(result);
    setCurrentPage(1);
  };

  useEffect(() => {
    applyAllFilters();
  }, [trades, filter, dateRange, customDateStart, customDateEnd, selectedTags, searchQuery, sortBy, sortOrder]);

  // ────────────────────────────────────────────────
  //  Computed values
  // ────────────────────────────────────────────────
  const currentTrades = filteredTrades.slice(
    (currentPage - 1) * tradesPerPage,
    currentPage * tradesPerPage
  );

  const cardClass      = theme === 'dark' ? 'bg-gray-800' : 'bg-white';
  const textClass      = theme === 'dark' ? 'text-white' : 'text-gray-900';
  const mutedTextClass = theme === 'dark' ? 'text-gray-400' : 'text-gray-500';

  // ────────────────────────────────────────────────
  //  Render
  // ────────────────────────────────────────────────
  return (
    <div className={`min-h-screen ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
      <div className="max-w-7xl mx-auto p-6">

        {/* Header */}
        <header className="flex justify-between items-center mb-8">
          <h1 className="text-3xl font-bold">Trading Journal</h1>
          <div className="flex gap-3 flex-wrap">
            <button onClick={() => setTheme(theme === 'dark' ? 'light' : 'dark')} className="p-2 rounded hover:bg-gray-700">
              {theme === 'dark' ? <Sun size={20} /> : <Moon size={20} />}
            </button>
            <button onClick={() => setReplayMode(!replayMode)} className="p-2 rounded hover:bg-gray-700">
              {replayMode ? 'Exit Replay' : 'Replay'}
            </button>
            <label className="flex items-center gap-2 p-2 rounded hover:bg-gray-700 cursor-pointer">
              <Upload size={20} /> Upload CSV
              <input type="file" accept=".csv" onChange={handleFileUpload} className="hidden" />
            </label>
            <button onClick={exportToCSV} className="flex items-center gap-2 p-2 rounded hover:bg-gray-700">
              <Download size={20} /> Export
            </button>
            <button onClick={() => setShowTradeForm(true)} className="flex items-center gap-2 px-4 py-2 bg-cyan-600 rounded-lg text-white">
              <Plus size={20} /> Add Trade
            </button>
            <button onClick={() => setShowGoalsModal(true)} className="flex items-center gap-2 p-2 rounded hover:bg-gray-700">
              <Target size={20} /> Goals
            </button>
          </div>
        </header>

        {uploadNotification && (
          <div className={`mb-6 p-4 rounded-lg ${uploadNotification.type === 'success' ? 'bg-green-900' : 'bg-amber-900'}`}>
            {uploadNotification.message}
            {uploadNotification.type === 'confirm' && (
              <div className="mt-3 flex gap-3">
                <button onClick={uploadNotification.onConfirm} className="px-4 py-2 bg-green-600 rounded">
                  Add {uploadNotification.newCount} trades
                </button>
                <button onClick={uploadNotification.onCancel} className="px-4 py-2 bg-gray-700 rounded">
                  Cancel
                </button>
              </div>
            )}
          </div>
        )}

        {/* ─── Main content ──────────────────────────────────────── */}
        {!replayMode ? (
          <>
            {/* Quick stats cards */}
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-5 mb-8">
              <div className={`${cardClass} p-5 rounded-xl shadow-sm`}>
                <h3 className="font-medium mb-1 text-gray-400">Total PnL</h3>
                <p className="text-2xl font-bold">${filteredTrades.reduce((s, t) => s + t.pnl, 0).toFixed(2)}</p>
              </div>
              <div className={`${cardClass} p-5 rounded-xl shadow-sm`}>
                <h3 className="font-medium mb-1 text-gray-400">Win Rate</h3>
                <p className="text-2xl font-bold">
                  {filteredTrades.length
                    ? (filteredTrades.filter(t => t.pnl > 0).length / filteredTrades.length * 100).toFixed(1)
                    : 0}%
                </p>
              </div>
              <div className={`${cardClass} p-5 rounded-xl shadow-sm`}>
                <h3 className="font-medium mb-1 text-gray-400">Trades</h3>
                <p className="text-2xl font-bold">{filteredTrades.length}</p>
              </div>
            </div>

            {/* Trade list */}
            <div className={`${cardClass} p-6 rounded-xl shadow-sm overflow-x-auto`}>
              <div className="flex justify-between items-center mb-4">
                <h2 className="text-xl font-semibold">Trades</h2>
                <div className="flex gap-3">
                  <input
                    type="text"
                    value={searchQuery}
                    onChange={e => setSearchQuery(e.target.value)}
                    placeholder="Search..."
                    className="bg-gray-700 border border-gray-600 rounded px-3 py-1.5"
                  />
                  <button onClick={() => setBulkMode(!bulkMode)} className="px-3 py-1.5 bg-gray-700 rounded">
                    {bulkMode ? 'Exit Bulk' : 'Bulk Edit'}
                  </button>
                </div>
              </div>

              <table className="w-full text-sm">
                <thead>
                  <tr className="border-b border-gray-700">
                    {bulkMode && <th className="w-8"></th>}
                    <th className="text-left py-3">Date</th>
                    <th className="text-left py-3">Symbol</th>
                    <th className="text-left py-3">Side</th>
                    <th className="text-right py-3">Qty</th>
                    <th className="text-right py-3">Entry</th>
                    <th className="text-right py-3">Exit</th>
                    <th className="text-right py-3">PnL</th>
                    <th className="text-left py-3">Tags</th>
                  </tr>
                </thead>
                <tbody>
                  {currentTrades.map(trade => (
                    <tr key={trade.id} className="border-b border-gray-800 hover:bg-gray-700/50">
                      {bulkMode && (
                        <td className="py-3">
                          <input
                            type="checkbox"
                            checked={selectedTrades.has(trade.id)}
                            onChange={() => {
                              const next = new Set(selectedTrades);
                              if (next.has(trade.id)) next.delete(trade.id);
                              else next.add(trade.id);
                              setSelectedTrades(next);
                            }}
                          />
                        </td>
                      )}
                      <td className="py-3">{trade.date}</td>
                      <td className="py-3 font-medium">{trade.symbol}</td>
                      <td className="py-3">
                        <span className={`px-2 py-0.5 rounded text-xs ${trade.side === 'long' ? 'bg-green-900 text-green-300' : 'bg-red-900 text-red-300'}`}>
                          {trade.side.toUpperCase()}
                        </span>
                      </td>
                      <td className="text-right py-3">{trade.quantity}</td>
                      <td className="text-right py-3">${trade.entryPrice.toFixed(2)}</td>
                      <td className="text-right py-3">${trade.exitPrice.toFixed(2)}</td>
                      <td className={`text-right py-3 font-medium ${trade.pnl >= 0 ? 'text-green-400' : 'text-red-400'}`}>
                        ${Math.abs(trade.pnl).toFixed(2)} {trade.pnl >= 0 ? '↑' : '↓'}
                      </td>
                      <td className="py-3">
                        {trade.tags?.map(tag => (
                          <span key={tag} className="inline-block bg-gray-700 text-gray-300 text-xs px-2 py-0.5 rounded mr-1.5 mb-1">
                            {tag}
                          </span>
                        ))}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>

              {/* Pagination */}
              {totalPages > 1 && (
                <div className="flex items-center justify-between mt-5">
                  <button
                    onClick={prevPage}
                    disabled={currentPage === 1}
                    className="px-4 py-2 bg-gray-700 rounded disabled:opacity-50"
                  >
                    Prev
                  </button>
                  <span className="text-sm text-gray-400">
                    Page {currentPage} of {totalPages}
                  </span>
                  <button
                    onClick={nextPage}
                    disabled={currentPage === totalPages}
                    className="px-4 py-2 bg-gray-700 rounded disabled:opacity-50"
                  >
                    Next
                  </button>
                </div>
              )}
            </div>
          </>
        ) : (
          <div className="text-center py-12">
            <h2 className="text-2xl mb-4">Replay Mode</h2>
            <p className="text-gray-400">Not implemented yet</p>
          </div>
        )}

        {/* Modals */}
        {showTradeForm && (
          <ManualTradeForm
            onSubmit={(data) => {
              setTrades(prev => [...prev, { ...data, id: 'm-' + Date.now() }]);
              setShowTradeForm(false);
            }}
            onClose={() => setShowTradeForm(false)}
            availableTags={availableTags}
          />
        )}

        {showGoalsModal && (
          <GoalsModal
            goals={dailyGoals}
            onSave={setDailyGoals}
            onClose={() => setShowGoalsModal(false)}
            theme={theme}
          />
        )}
      </div>
    </div>
  );
};

// ────────────────────────────────────────────────
//  Modals (minimal versions)
// ────────────────────────────────────────────────
function ManualTradeForm({ onSubmit, onClose, availableTags }) {
  const [form, setForm] = useState({
    date: new Date().toISOString().slice(0,10),
    symbol: '',
    side: 'long',
    quantity: '',
    entryPrice: '',
    exitPrice: '',
    fees: '0',
    notes: '',
    tags: []
  });

  const handleSubmit = e => {
    e.preventDefault();
    onSubmit({
      ...form,
      quantity: Number(form.quantity),
      entryPrice: Number(form.entryPrice),
      exitPrice: Number(form.exitPrice),
      fees: Number(form.fees),
      pnl: form.side === 'long'
        ? (form.exitPrice - form.entryPrice) * form.quantity - form.fees
        : (form.entryPrice - form.exitPrice) * form.quantity - form.fees
    });
  };

  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50">
      <div className="bg-gray-800 rounded-xl p-6 w-full max-w-lg">
        <h2 className="text-xl font-bold mb-5">New Trade</h2>
        <form onSubmit={handleSubmit} className="space-y-4">
          <input type="date" value={form.date} onChange={e => setForm({...form, date: e.target.value})} className="w-full bg-gray-700 p-3 rounded" />
          <input placeholder="Symbol" value={form.symbol} onChange={e => setForm({...form, symbol: e.target.value.toUpperCase()})} className="w-full bg-gray-700 p-3 rounded" />
          <select value={form.side} onChange={e => setForm({...form, side: e.target.value})} className="w-full bg-gray-700 p-3 rounded">
            <option value="long">Long</option>
            <option value="short">Short</option>
          </select>
          <input type="number" placeholder="Quantity" value={form.quantity} onChange={e => setForm({...form, quantity: e.target.value})} className="w-full bg-gray-700 p-3 rounded" />
          <input type="number" step="0.01" placeholder="Entry" value={form.entryPrice} onChange={e => setForm({...form, entryPrice: e.target.value})} className="w-full bg-gray-700 p-3 rounded" />
          <input type="number" step="0.01" placeholder="Exit" value={form.exitPrice} onChange={e => setForm({...form, exitPrice: e.target.value})} className="w-full bg-gray-700 p-3 rounded" />
          <input type="number" step="0.01" placeholder="Fees" value={form.fees} onChange={e => setForm({...form, fees: e.target.value})} className="w-full bg-gray-700 p-3 rounded" />
          <div>
            <label className="block mb-1 text-sm">Tags</label>
            <select multiple value={form.tags} onChange={e => setForm({...form, tags: [...e.target.selectedOptions].map(o => o.value)})} className="w-full bg-gray-700 p-3 rounded">
              {availableTags.map(tag => <option key={tag} value={tag}>{tag}</option>)}
            </select>
          </div>
          <div className="flex gap-3 pt-2">
            <button type="submit" className="flex-1 bg-cyan-600 py-3 rounded font-medium">Save Trade</button>
            <button type="button" onClick={onClose} className="flex-1 bg-gray-700 py-3 rounded">Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}

function GoalsModal({ goals, onSave, onClose, theme }) {
  const [form, setForm] = useState(goals);

  const handleSubmit = e => {
    e.preventDefault();
    onSave(form);
    onClose();
  };

  return (
    <div className="fixed inset-0 bg-black/70 flex items-center justify-center z-50">
      <div className={`${theme === 'dark' ? 'bg-gray-900' : 'bg-white'} rounded-xl p-6 w-full max-w-md`}>
        <div className="flex justify-between items-center mb-5">
          <h2 className="text-xl font-bold">Daily Goals</h2>
          <button onClick={onClose}><X /></button>
        </div>
        <form onSubmit={handleSubmit} className="space-y-5">
          <div>
            <label className="block text-sm mb-1.5">Max Daily Loss</label>
            <input type="number" value={form.maxLoss} onChange={e => setForm({...form, maxLoss: Number(e.target.value)})} className="w-full p-3 bg-gray-800 rounded" />
          </div>
          <div>
            <label className="block text-sm mb-1.5">Profit Target</label>
            <input type="number" value={form.targetProfit} onChange={e => setForm({...form, targetProfit: Number(e.target.value)})} className="w-full p-3 bg-gray-800 rounded" />
          </div>
          <div>
            <label className="block text-sm mb-1.5">Max Trades / Day</label>
            <input type="number" value={form.maxTrades} onChange={e => setForm({...form, maxTrades: Number(e.target.value)})} className="w-full p-3 bg-gray-800 rounded" />
          </div>
          <div className="flex gap-3">
            <button type="submit" className="flex-1 bg-cyan-600 py-3 rounded">Save</button>
            <button type="button" onClick={onClose} className="flex-1 bg-gray-700 py-3 rounded">Cancel</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default TradingJournal;
